    <script>
        // Import necessary VTK.js modules
        const vtkFullScreenRenderWindow = vtk.Rendering.Misc.vtkFullScreenRenderWindow;
        const vtkVolume = vtk.Rendering.Core.vtkVolume;
        const vtkVolumeMapper = vtk.Rendering.Core.vtkVolumeMapper;
        const vtkColorTransferFunction = vtk.Rendering.Core.vtkColorTransferFunction;
        const vtkPiecewiseFunction = vtk.Common.DataModel.vtkPiecewiseFunction;
        const vtkHttpDataSetReader = vtk.IO.Core.vtkHttpDataSetReader;

        // Initialize the full-screen render window
        const fullScreenRenderWindow = vtkFullScreenRenderWindow.newInstance({
            background: [0, 0, 0],
        });
        const renderWindow = fullScreenRenderWindow.getRenderWindow();
        const renderer = fullScreenRenderWindow.getRenderer();

        // Load the dataset from the specified URL
        const reader = vtkHttpDataSetReader.newInstance({ fetchGzip: true });
        reader.setUrl('http://127.0.0.1:5000/dataset/airfoil_oneslice.vtp').then(() => {
            reader.loadData().then(() => {
                const dataset = reader.getOutputData();

                // Create a volume mapper and set the input data
                const volumeMapper = vtkVolumeMapper.newInstance();
                volumeMapper.setInputData(dataset);

                // Create a volume actor and set the mapper
                const volumeActor = vtkVolume.newInstance();
                volumeActor.setMapper(volumeMapper);

                // Create a color transfer function for scalar mapping
                const colorTransferFunction = vtkColorTransferFunction.newInstance();
                colorTransferFunction.addRGBPoint(0.0, 0.0, 0.0, 0.0);
                colorTransferFunction.addRGBPoint(1.0, 1.0, 1.0, 1.0);

                // Create a piecewise function for opacity mapping
                const piecewiseFunction = vtkPiecewiseFunction.newInstance();
                piecewiseFunction.addPoint(0.0, 0.0);
                piecewiseFunction.addPoint(1.0, 1.0);

                // Set the color and opacity functions to the volume actor
                volumeActor.getProperty().setRGBTransferFunction(0, colorTransferFunction);
                volumeActor.getProperty().setScalarOpacity(0, piecewiseFunction);

                // Add the volume actor to the renderer
                renderer.addVolume(volumeActor);

                // Position the camera to focus on the dataset bounds
                renderer.resetCamera();

                // Render the scene
                renderWindow.render();
            });
        });
    </script>
</body>
</html>
