<script>
  // 引入所需的 VTK 类
  const vtkFullScreenRenderWindow = vtk.Rendering.Misc.vtkFullScreenRenderWindow;
  const vtkHttpDataSetReader = vtk.IO.Core.vtkHttpDataSetReader;
  const vtkImageShiftScale = vtk.Filters.General.vtkImageShiftScale;
  const vtkImageSliceMapper = vtk.Rendering.Core.vtkImageSliceMapper;
  const vtkImageSlice = vtk.Rendering.Core.vtkImageSlice;
  const vtkRenderer = vtk.Rendering.Core.vtkRenderer;
  const vtkRenderWindow = vtk.Rendering.Core.vtkRenderWindow;
  const vtkColorTransferFunction = vtk.Rendering.Core.vtkColorTransferFunction;
  const vtkPiecewiseFunction = vtk.Common.DataModel.vtkPiecewiseFunction;

  const vtkCalculator = vtk.Filters.General.vtkCalculator;

  // 初始化渲染器和交互器
  const fullScreenRenderer = vtkFullScreenRenderWindow.newInstance({
    rootContainer: document.body,
    renderWindowParent: document.getElementById('view'),
  });

  const renderer = vtkRenderer.newInstance();
  const renderWindow = fullScreenRenderer.getRenderWindow();

  renderWindow.addRenderer(renderer);
  fullScreenRenderer.setResizeCallback(() => {}); // 自适应窗口大小

  // 加载 VTI 数据（异步）
  const reader = vtkHttpDataSetReader.newInstance({ fetchGzip: true });
  reader.setUrl('http://127.0.0.1:5000/dataset/rotor_simplified.vti').then(() => {
    reader.update();

    const imageData = reader.getOutputData();

    // 计算速度大小：sqrt(vx^2 + vy^2 + vz^2)
    const calculator = vtkCalculator.newInstance();
    calculator.setInputData(imageData);
    calculator.setFormula({
      // 假设速度场的向量数据名称为 "velocity"
      getArrays: (inDatasets) => ({
        input: [{ location: 'PointData', name: 'velocity' }],
        output: [{ location: 'PointData', name: 'velocityMagnitude', dataType: 'Float32Array', numberOfComponents: 1 }],
      }),
      evaluate: (arraysIn, arraysOut) => {
        const velocity = arraysIn[0].getData();
        const magnitude = new Float32Array(arraysIn[0].getNumberOfValues() / 3);
        for (let i = 0; i < magnitude.length; i++) {
          const x = velocity[i * 3];
          const y = velocity[i * 3 + 1];
          const z = velocity[i * 3 + 2];
          magnitude[i] = Math.sqrt(x * x + y * y + z * z);
        }
        arraysOut[0].getData().set(magnitude);
      },
    });
    calculator.update();
    const magImageData = calculator.getOutputData();

    // 创建三个切片分别对应 X/Y/Z 方向
    const slices = ['x', 'y', 'z'].map((axis) => {
      const mapper = vtkImageSliceMapper.newInstance();
      mapper.setInputData(magImageData);
      mapper.setSliceAtFocalPoint(true);
      mapper.setOrientationToAxis(['x','y','z'].indexOf(axis));

      const slice = vtkImageSlice.newInstance();
      slice.setMapper(mapper);

      return { mapper, slice };
    });

    // 添加所有切片到场景中
    slices.forEach(({ slice }) => renderer.addActor(slice));

    // 设置颜色映射函数
    const ctfun = vtkColorTransferFunction.newInstance();
    const setCTF = (scheme) => {
      ctfun.removeAllPoints();
      if (scheme === 'blue-red') {
        ctfun.addRGBPoint(0, 0, 0, 1);
        ctfun.addRGBPoint(1, 1, 0, 0);
      } else if (scheme === 'rainbow') {
        ctfun.addRGBPoint(0, 0, 0, 1);
        ctfun.addRGBPoint(0.3, 0, 1, 1);
        ctfun.addRGBPoint(0.6, 1, 1, 0);
        ctfun.addRGBPoint(1, 1, 0, 0);
      } else if (scheme === 'grayscale') {
        ctfun.addRGBPoint(0, 0, 0, 0);
        ctfun.addRGBPoint(1, 1, 1, 1);
      }
      slices.forEach(({ slice }) => slice.getProperty().setColorTransferFunction(ctfun));
      renderWindow.render();
    };

    // 初始颜色方案
    setCTF('blue-red');

    // 同步切片位置与滑块
    const updateSlices = () => {
      const dims = magImageData.getDimensions();
      const x = Math.floor(dims[0] * (document.getElementById('xSlider').value / 100));
      const y = Math.floor(dims[1] * (document.getElementById('ySlider').value / 100));
      const z = Math.floor(dims[2] * (document.getElementById('zSlider').value / 100));
      slices[0].mapper.setSliceNumber(x);
      slices[1].mapper.setSliceNumber(y);
      slices[2].mapper.setSliceNumber(z);
      renderWindow.render();
    };

    // 绑定事件监听器
    ['xSlider', 'ySlider', 'zSlider'].forEach(id =>
      document.getElementById(id).addEventListener('input', updateSlices)
    );

    document.getElementById('colorScheme').addEventListener('change', (e) => {
      setCTF(e.target.value);
    });

    // 初始渲染
    renderer.resetCamera();
    renderWindow.render();
  }).catch((err) => {
