        const vtkImageMapper = vtk.Rendering.Core.vtkImageMapper;
        const vtkColorTransferFunction = vtk.Rendering.Core.vtkColorTransferFunction;
        const vtkOrientationMarkerWidget = vtk.Interaction.Widgets.vtkOrientationMarkerWidget;

        // Create render window
        const fullScreenRenderer = vtkFullScreenRenderWindow.newInstance({ background: [0, 0, 0] });
        const renderer = fullScreenRenderer.getRenderer();
        const renderWindow = fullScreenRenderer.getRenderWindow();

        // Load rotor dataset from URL
        const reader = vtkHttpDataSetReader.newInstance({ fetchGzip: true });
        reader.setUrl('http://127.0.0.1:5000/dataset/rotor.vti').then(() => {
            reader.loadData().then(() => {
                const imageData = reader.getOutputData();

                // Set "Pressure" as active scalar array
                const pointData = imageData.getPointData();
                pointData.setActiveScalars('Pressure');
                const range = pointData.getScalars().getRange();

                // Create color transfer function (blue-white-red)
                const lut = vtkColorTransferFunction.newInstance();
                lut.addRGBPoint(range[0], 0, 0, 1);  // Blue at minimum
                lut.addRGBPoint((range[0] + range[1])/2, 1, 1, 1); // White at middle
                lut.addRGBPoint(range[1], 1, 0, 0);  // Red at maximum

                // Create mapper and set input with color transfer function
                const mapper = vtkImageMapper.newInstance();
                mapper.setInputData(imageData);
                mapper.setScalarRange(range[0], range[1]);
                mapper.setColorLookupTable(lut);

                // Create slice actor
                const actor = vtkImageSlice.newInstance();
                actor.setMapper(mapper);

                // Calculate slice index at 95% Y depth
                const dims = imageData.getDimensions();
                const sliceIndex = Math.floor(dims[1] * 0.95);
                mapper.setSlice(sliceIndex);
                mapper.setSlabTypeToSingle();

                // Add actor to renderer
                renderer.addActor(actor);

                // Add orientation marker
                const axes = vtk.Rendering.Core.vtkAnnotatedCubeActor.newInstance();
                axes.setXPlusFaceProperty({ text: '+X' });
                axes.setXMinusFaceProperty({ text: '-X' });
                axes.setYPlusFaceProperty({ text: '+Y' });
                axes.setYMinusFaceProperty({ text: '-Y' });
                axes.setZPlusFaceProperty({ text: '+Z' });
                axes.setZMinusFaceProperty({ text: '-Z' });

                const widget = vtkOrientationMarkerWidget.newInstance({
                    actor: axes,
                    interactor: renderWindow.getInteractor()
                });
                widget.setEnabled(true);
                widget.setViewportCorner(
                    vtkOrientationMarkerWidget.Corners.BOTTOM_RIGHT
                );

                // Render
                renderer.resetCamera();
                renderWindow.render();
            });
        });
    </script>
</body>
</html>
