<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>Rotor Visualization</title>
</head>
<body>
    <script src="https://unpkg.com/vtk.js"></script>
    <script>
        // Modules required
        const vtkFullScreenRenderWindow = vtk.Rendering.Misc.vtkFullScreenRenderWindow;
        const vtkHttpDataSetReader = vtk.IO.Core.vtkHttpDataSetReader;
        const vtkImageSlice = vtk.Rendering.Core.vtkImageSlice;
        const vtkImageMapper = vtk.Rendering.Core.vtkImageMapper;
        const vtkColorTransferFunction = vtk.Rendering.Core.vtkColorTransferFunction;
        const vtkOrientationMarkerWidget = vtk.Interaction.Widgets.vtkOrientationMarkerWidget;
        const vtkAnnotatedCubeActor = vtk.Rendering.Core.vtkAnnotatedCubeActor;

        // Create render window
        const fullScreenRenderer = vtkFullScreenRenderWindow.newInstance({ background: [0, 0, 0] });
        const renderer = fullScreenRenderer.getRenderer();
        const renderWindow = fullScreenRenderer.getRenderWindow();

        // Load the rotor dataset
        const reader = vtkHttpDataSetReader.newInstance();
        reader.setUrl('http://127.0.0.1:5000/dataset/rotor.vti').then(() => {
            reader.loadData().then(() => {
                const dataset = reader.getOutputData();

                // Set active scalar to "Pressure"
                dataset.getPointData().setActiveScalars('Pressure');

                // Get scalar range for color mapping
                const scalarRange = dataset.getPointData().getScalars().getRange();

                // Create color transfer function
                const colorTransferFunction = vtkColorTransferFunction.newInstance();
                colorTransferFunction.addRGBPoint(scalarRange[0], 0, 0, 1); // Blue at min
                colorTransferFunction.addRGBPoint((scalarRange[0] + scalarRange[1]) / 2, 1, 1, 1); // White at mid
                colorTransferFunction.addRGBPoint(scalarRange[1], 1, 0, 0); // Red at max

                // Create mapper
                const mapper = vtkImageMapper.newInstance();
                mapper.setInputData(dataset);
                mapper.setSliceAtFocalPoint(true);

                // Calculate Y slice index (95% depth)
                const extent = dataset.getExtent();
                const ySliceIndex = Math.floor(extent[2] + (extent[3] - extent[2]) * 0.95);
                mapper.setYSlice(ySliceIndex);

                // Create actor
                const actor = vtkImageSlice.newInstance();
                actor.setMapper(mapper);
                actor.getProperty().setRGBTransferFunction(colorTransferFunction);
                actor.getProperty().setOpacity(1.0); // Fully opaque

                // Add actor to renderer
                renderer.addActor(actor);

                // Add orientation marker
                const axes = vtkAnnotatedCubeActor.newInstance();
                axes.setDefaultStyle({
                    text: '+X',
                    fontStyle: 'bold',
                    fontFamily: 'Arial',
                    fontColor: [0, 0, 0],
                    fontSizeScale: (res) => res / 2,
                    faceColor: '#0000ff',
                    faceRotation: 0,
                    edgeThickness: 0,
                    edgeColor: [0, 0, 0],
                });
                axes.setXPlusFaceProperty({ text: '+X', faceColor: '#ffff00' });
                axes.setXMinusFaceProperty({ text: '-X', faceColor: '#ff0000' });
                axes.setYPlusFaceProperty({ text: '+Y', faceColor: '#00ff00' });
                axes.setYMinusFaceProperty({ text: '-Y', faceColor: '#0000ff' });
                axes.setZPlusFaceProperty({ text: '+Z', faceColor: '#ffffff' });
                axes.setZMinusFaceProperty({ text: '-Z', faceColor: '#ff00ff' });
                axes.setCubeUnitLength(20);

                const widget = vtkOrientationMarkerWidget.newInstance({
                    actor: axes,
                    interactor: renderWindow.getInteractor(),
                });
                widget.setEnabled(true);
                widget.setViewportCorner(vtkOrientationMarkerWidget.Corners.BOTTOM_RIGHT);
                widget.setViewportSize(0.15);

                renderer.resetCamera();
                renderWindow.render();
            });
        });
    </script>
</body>
</html>
