<!DOCTYPE html>
<html>
<head>
  <title>VTK.js Rotor Visualization</title>
  <script src="https://unpkg.com/vtk.js"></script>
  <style>
    body {
      margin: 0;
    }
  </style>
</head>
<body>
  <div id="container"></div>
  <script>
    // Main function that runs when the page loads
    function main() {
      // Get the container for VTK.js rendering
      const container = document.getElementById('container');

      // Create the VTK.js rendering pipeline
      const fullScreenRenderer = vtk.Rendering.Misc.vtkFullScreenRenderWindow.newInstance({
        rootContainer: container,
      });
      const renderer = fullScreenRenderer.getRenderer();
      const renderWindow = fullScreenRenderer.getRenderWindow();

      // Create and setup the reader for the VTI file
      const reader = vtk.IO.XML.vtkXMLImageDataReader.newInstance();

      // Load the dataset from the provided URL
      reader.setUrl('http://127.0.0.1:5000/dataset/rotor.vti').then(() => {
        // Pipeline setup
        const source = reader.getOutputData();

        // Get dataset info for slice calculation
        const dims = source.getDimensions();

        // Calculate slice index (95% of Y dimension)
        const sliceIndex = Math.floor(dims[1] * 0.95);

        // Create slice filter
        const slice = vtk.Filters.General.vtkImageSliceFilter.newInstance();
        slice.setInputData(source);
        slice.setSliceModeToY();
        slice.setSlice(sliceIndex);

        // Create mapper and set active scalar to "Pressure"
        const mapper = vtk.Rendering.Core.vtkMapper.newInstance();
        mapper.setInputConnection(slice.getOutputPort());
        mapper.setScalarVisibility(true);
        mapper.setScalarModeToUsePointFieldData();
        mapper.selectColorArray('Pressure');

        // Get scalar range for colormap
        const dataRange = source.getPointData().getArrayByName('Pressure').getRange();

        // Create color transfer function (blue to white to red)
        const lookupTable = vtk.Rendering.Core.vtkColorTransferFunction.newInstance();
        lookupTable.addRGBPoint(dataRange[0], 0.0, 0.0, 1.0);       // Blue at min
        lookupTable.addRGBPoint((dataRange[0] + dataRange[1])/2, 1.0, 1.0, 1.0); // White at midpoint
        lookupTable.addRGBPoint(dataRange[1], 1.0, 0.0, 0.0);       // Red at max
        mapper.setLookupTable(lookupTable);

        // Create actor
        const actor = vtk.Rendering.Core.vtkActor.newInstance();
        actor.setMapper(mapper);
        actor.getProperty().setOpacity(1.0);  // Fully opaque as requested

        // Add actor to renderer
        renderer.addActor(actor);

        // Add orientation axes widget
        const axes = vtk.Rendering.Core.vtkOrientationMarkerWidget.newInstance({
          actor: vtk.Rendering.Core.vtkAnnotatedCubeActor.newInstance({
            faceColors: [
              [1.0, 0.0, 0.0], // +X face
              [0.0, 1.0, 0.0], // -X face
              [0.0, 0.0, 1.0], // +Y face
              [1.0, 1.0, 0.0], // -Y face
              [1.0, 0.0, 1.0], // +Z face
              [0.0, 1.0, 1.0], // -Z face
            ],
            edgeThickness: 0.1,
          }),
          interactor: fullScreenRenderer.getInteractor(),
        });
        axes.setEnabled(true);
        axes.setViewportCorner(
          vtk.Rendering.Core.vtkOrientationMarkerWidget.Corners.BOTTOM_RIGHT
        );
        axes.setViewportSize(0.15);

        // Reset camera and render
        renderer.resetCamera();
        renderWindow.render();
      });
    }

    // Execute the main function when the page loads
    window.onload = main;
  </script>
</body>
</html>
