        const vtkImageSlice = vtk.Rendering.Core.vtkImageSlice;// Actor for rendering 2D image slices
        const vtkImageMapper = vtk.Rendering.Core.vtkImageMapper;
        const vtkImageData = vtk.Common.DataModel.vtkImageData;

