<!DOCTYPE html>
<html>
<head>
  <title>Volume Rendering with VTK.js</title>
  <script src="https://unpkg.com/vtk.js"></script>
</head>
<body>
  <div id="container"></div>

  <script>
    // Main rendering function
    async function render() {
      // Create the VTK.js pipeline container
      const container = document.querySelector('#container');
      const fullScreenRenderer = vtk.Rendering.Misc.vtkFullScreenRenderWindow.newInstance({
        rootContainer: container,
      });
      const renderer = fullScreenRenderer.getRenderer();
      const renderWindow = fullScreenRenderer.getRenderWindow();

      // Create reader for VTP file
      const reader = vtk.IO.XML.vtkXMLPolyDataReader.newInstance();

      try {
        // Fetch and load the dataset
        const response = await fetch('http://127.0.0.1:5000/dataset/airfoil_oneslice.vtp');
        const data = await response.arrayBuffer();
        reader.parseAsArrayBuffer(data);

        // Get the output data
        const polydata = reader.getOutputData(0);

        // Create mapper and set the scalar array for coloring
        const mapper = vtk.Rendering.Core.vtkMapper.newInstance();
        mapper.setInputData(polydata);
        mapper.setScalarVisibility(true);
        mapper.setScalarModeToUsePointFieldData();
        mapper.selectColorArray('p'); // Using 'p' scalar array for color encoding
        mapper.setUseLookupTableScalarRange(true);

        // Create actor and set the mapper
        const actor = vtk.Rendering.Core.vtkActor.newInstance();
        actor.setMapper(mapper);

        // Add actor to the renderer
        renderer.addActor(actor);

        // Set up lookup table for color mapping
        const lookupTable = vtk.Rendering.Core.vtkColorTransferFunction.newInstance();
        lookupTable.applyColorMap(vtk.Common.Core.vtkColorMaps.getPresetByName('jet'));
        mapper.setLookupTable(lookupTable);

        // Reset camera to fit the dataset bounds
        renderer.resetCamera();

        // Render the scene
        renderWindow.render();
      } catch (error) {
        console.error('Error loading or rendering data:', error);
      }
    }

    // Start the rendering process when the page loads
    document.addEventListener('DOMContentLoaded', render);
  </script>
</body>
</html>
