<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Isabel Dataset Streamline Visualization</title>
    <script src="https://unpkg.com/vtk.js"></script>
</head>
<body>
    <div id="container"></div>

    <script>
        // Create full screen render window
        const fullScreenRenderer = vtk.Rendering.Misc.vtkFullScreenRenderWindow.newInstance({
            rootContainer: document.getElementById('container'),
            background: [0, 0, 0]
        });
        const renderer = fullScreenRenderer.getRenderer();
        const renderWindow = fullScreenRenderer.getRenderWindow();

        // Create reader for Isabel dataset
        const reader = vtk.IO.Core.vtkHttpDataSetReader.newInstance({ fetchGzip: true });
        reader.setUrl('http://127.0.0.1:5000/dataset/isabel.vti', { loadData: true }).then(() => {
            const imageData = reader.getOutputData();

            // Create outline filter for the dataset
            const outline = vtk.Filters.General.vtkOutlineFilter.newInstance();
            outline.setInputData(imageData);

            // Create outline mapper and actor
            const outlineMapper = vtk.Rendering.Core.vtkMapper.newInstance();
            outlineMapper.setInputConnection(outline.getOutputPort());

            const outlineActor = vtk.Rendering.Core.vtkActor.newInstance();
            outlineActor.setMapper(outlineMapper);
            outlineActor.getProperty().setColor(1, 0, 0); // Red outline
            outlineActor.getProperty().setLineWidth(2); // Set outline width

            // Create streamline source
            const seeds = vtk.Filters.Sources.vtkPointSource.newInstance();
            const center = imageData.getCenter();
            const bounds = imageData.getBounds();
            const spacing = imageData.getSpacing();

            // Configure seed points at center with good density
            seeds.setNumberOfPoints(100);
            seeds.setRadius(
                (bounds[1] - bounds[0]) * 0.25,
                (bounds[3] - bounds[2]) * 0.25,
                (bounds[5] - bounds[4]) * 0.25
            );
            seeds.setCenter(center);

            // Create streamline filter
            const streamTracer = vtk.Filters.General.vtkStreamTracer.newInstance();
            streamTracer.setInputData(imageData);
            streamTracer.setSourceConnection(seeds.getOutputPort());
            streamTracer.setMaximumPropagation(1000); // Long enough to cover domain
            streamTracer.setIntegrationDirection(vtk.Filters.General.vtkStreamTracer.IntegrationDirections.FORWARD);
            streamTracer.setComputeVorticity(true);
            streamTracer.setInitialIntegrationStep(0.2);
            streamTracer.setIntegratorType(vtk.Filters.General.vtkStreamTracer.IntegratorTypes.RUNGE_KUTTA4);

            // Set velocity field
            streamTracer.setInputArrayToProcess(0, 0, 0, 0, 'Velocity');

            // Create streamline mapper and actor
            const streamMapper = vtk.Rendering.Core.vtkPolyDataMapper.newInstance();
            streamMapper.setInputConnection(streamTracer.getOutputPort());

            const streamActor = vtk.Rendering.Core.vtkActor.newInstance();
            streamActor.setMapper(streamMapper);
            streamActor.getProperty().setColor(0, 1, 1); // Cyan streamlines
            streamActor.getProperty().setLineWidth(2); // Set streamline width

            // Add actors to renderer
            renderer.addActor(outlineActor);
            renderer.addActor(streamActor);

            // Reset camera and render
            renderer.resetCamera();
            renderWindow.render();
        });
    </script>
</body>
</html>
