<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Rotor Visualization</title>
</head>
<body>
    <div id="renderer"></div>
    <script src="https://unpkg.com/vtk.js"></script>
    <script>
        // Initialize full screen render window
        const fullScreenRenderer = vtk.Rendering.Misc.vtkFullScreenRenderWindow.newInstance({
            background: [0, 0, 0],
        });
        const renderer = fullScreenRenderer.getRenderer();
        const renderWindow = fullScreenRenderer.getRenderWindow();

        // Create reader for VTI file
        const reader = vtk.IO.Core.vtkHttpDataSetReader.newInstance({ fetchGzip: true });
        reader.setUrl('http://127.0.0.1:5000/dataset/rotor.vti', { loadData: true })
            .then(() => {
                // Get the image data
                const imageData = reader.getOutputData();

                // Set active scalar to "Pressure"
                const pointData = imageData.getPointData();
                pointData.setActiveScalars("Pressure");

                // Create slice filter
                const sliceFilter = vtk.Filters.General.vtkImageSliceFilter.newInstance();
                sliceFilter.setInputData(imageData);

                // Calculate slice index (95% of Y extent)
                const extent = imageData.getExtent();
                const ySliceIndex = Math.round(extent[2] + (extent[3] - extent[2]) * 0.95);
                sliceFilter.setSlice(ySliceIndex);
                sliceFilter.setSliceOrientationToY();

                // Create mapper and actor
                const mapper = vtk.Rendering.Core.vtkMapper.newInstance();
                mapper.setInputConnection(sliceFilter.getOutputPort());
                mapper.setScalarRange(pointData.getScalars().getRange());

                const actor = vtk.Rendering.Core.vtkActor.newInstance();
                actor.setMapper(mapper);

                // Create color transfer function (blue -> white -> red)
                const cfun = vtk.Rendering.Core.vtkColorTransferFunction.newInstance();
                const range = pointData.getScalars().getRange();
                cfun.addRGBPoint(range[0], 0, 0, 1);  // Blue at min
                cfun.addRGBPoint((range[0] + range[1])/2, 1, 1, 1);  // White at mid
                cfun.addRGBPoint(range[1], 1, 0, 0);  // Red at max

                // Set color and opacity
                mapper.setLookupTable(cfun);
                actor.getProperty().setOpacity(1.0);  // Fully opaque

                // Add actor to renderer
                renderer.addActor(actor);

                // Add orientation axes
                const axes = vtk.Rendering.Core.vtkAnnotatedCubeActor.newInstance();
                axes.setDefaultStyle({
                    text: '+X',
                    fontStyle: 'bold',
                    fontFamily: 'Arial',
                    fontColor: [0, 0, 0],
                    fontSizeScale: (res) => res / 2,
                    faceColor: '#FF0000',
                    faceRotation: 0,
                    edgeThickness: 0.1,
                    edgeColor: [0, 0, 0],
                });
                axes.setXPlusFaceProperty({ text: '+X' });
                axes.setXMinusFaceProperty({ text: '-X' });
                axes.setYPlusFaceProperty({ text: '+Y' });
                axes.setYMinusFaceProperty({ text: '-Y' });
                axes.setZPlusFaceProperty({ text: '+Z' });
                axes.setZMinusFaceProperty({ text: '-Z' });
                axes.setDefaultStyle({ fontColor: 'white' });

                const widget = vtk.Interaction.Widgets.vtkOrientationMarkerWidget.newInstance({
                    actor: axes,
                    interactor: fullScreenRenderer.getInteractor(),
                });
                widget.setEnabled(true);
                widget.setViewportCorner(vtk.Interaction.Widgets.vtkOrientationMarkerWidget.Corners.BOTTOM_RIGHT);
                widget.setViewportSize(0.15);

                // Reset camera and render
                renderer.resetCamera();
                renderWindow.render();
            });
    </script>
