        reader.setUrl('http://127.0.0.1:5000/dataset/redsea.vti', { loadData: true }).then(() => {
            const data = reader.getOutputData();

            // Extract velocity array and compute magnitude
            const velocity = data.getPointData().getArrayByName('velocity');
            const velocityMagnitude = new Float32Array(velocity.getNumberOfTuples());
            for (let i =  izeForMagnitude3D(velocity, velocityMagnitude);
            const newArray = vtk.Common.Core.vtkDataArray.newInstance({
                name: 'velocityMagnitude',
                values: velocityMagnitude,
                numberOfComponents: 1
            });
            data.getPointData().addArray(newArray);
            data.getPointData().setActiveScalars('velocityMagnitude');

            // Set up volume rendering
            const mapper = vtk.Rendering.Core.vtkVolumeMapper.newInstance();
            mapper.setInputData(data);
            mapper.setSampleDistance(0.7);

            // Create the volume actor
            const volume = vtk.Rendering.Core.vtkVolume.newInstance();
            volume.setMapper(mapper);

            // Set up color transfer function (blue -> white -> red)
            const ctf = vtk.Rendering.Core.vtkColorTransferFunction.newInstance();
            const range = data.getPointData().getScalars().getRange();
            ctf.addRGBPoint(range[0], 0.0, 0.0, 1.0);  // Blue
            ctf.addRGBPoint((range[0] + range[1]) * 0.5, 1.0, 1.0, 1.0);  // White
            ctf.addRGBPoint(range[1], 1.0, 0.0, 0.0);  // Red

            // Set up opacity transfer function
            const otf = vtk.Common.DataModel.vtkPiecewiseFunction.newInstance();
            otf.addPoint(range[0], 0.0);
            otf.addPoint(range[1], 0.2);

            // Apply transfer functions and shading properties
            const volumeProperty = volume.getProperty();
            volumeProperty.setRGBTransferFunction(0, ctf);
            volumeProperty.setScalarOpacity(0, otf);
            volumeProperty.setShade(true);
            volumeProperty.setAmbient(0.2);
            volumeProperty.setDiffuse(0.7);
            volumeProperty.setSpecular(0.3);
            volumeProperty.setSpecularPower(8);

            // Add volume to renderer and reset camera
            renderer.addVolume(volume);

            // Adjust camera to look along +Z axis and center on dataset
            const camera = renderer.getActiveCamera();
            const bounds = data.getBounds();
            const center = [
                (bounds[0] + bounds[1]) * 0.5,
                (bounds[2] + bounds[3]) * 0.5,
                (bounds[4] + bounds[5]) * 0.5
            ];
            camera.setFocalPoint(center);
            camera.setPosition(center[0], center[1], center[2] + 1);
            camera.setViewUp(0, 1, 0);
            camera.setParallelProjection(false);
            renderer.resetCamera();
            renderWindow.render();
        });

        // Helper function to compute magnitude of 3D vectors
        function computeMagnitude3D(inputArray, outputArray) {
            for (let i = 0; i < inputArray.getNumberOfTuples(); i++) {
                const vx = inputArray.getComponent(i, 0);
                const vy = inputArray.getComponent(i, 1);
                const vz = inputArray.getComponent(i, 2);
                outputArray[i] = Math.sqrt(vx * vx + vy * vy + vz * vz);
