<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Rotor Visualization with vtk.js</title>
</head>
<body>
    <div id="renderer"></div>

    <script src="https://unpkg.com/vtk.js"></script>
    <script>
        // Initialize full screen render window
        const fullScreenRenderer = vtk.Rendering.Misc.vtkFullScreenRenderWindow.newInstance();
        const renderer = fullScreenRenderer.getRenderer();
        const renderWindow = fullScreenRenderer.getRenderWindow();

        // Create reader to load the dataset
        const reader = vtk.IO.Core.vtkHttpDataSetReader.newInstance({ fetchGzip: true });

        // Set up slice filter for Y-axis
        const slice = vtk.Filters.General.vtkSliceFilter.newInstance();
        slice.setInputConnection(reader.getOutputPort());

        // Load the dataset and process it
        reader.setUrl('http://127.0.0.1:5000/dataset/rotor.vti', { loadData: true }).then(() => {
            // Get dataset info
            const dataset = reader.getOutputData();
            const dataRange = dataset.getPointData().getScalars('Pressure').getRange();
            const yDims = dataset.getDimensions()[1];

            // Calculate slice index at 95% depth along Y-axis
            const ySliceIndex = Math.floor(yDims * 0.95);
            slice.setSliceNormal(0, 1, 0); // Y-axis normal
            slice.setSliceOrigin(0, dataset.getBounds()[2 + ySliceIndex * 2], 0);

            // Create mapper and actor for the slice
            const mapper = vtk.Rendering.Core.vtkMapper.newInstance();
            mapper.setInputConnection(slice.getOutputPort());
            mapper.setScalarVisibility(true);
            mapper.setScalarModeToUsePointData();
            mapper.setColorModeToMapScalars();
            mapper.setScalarRange(dataRange[0], dataRange[1]);

            const actor = vtk.Rendering.Core.vtkActor.newInstance();
            actor.setMapper(mapper);

            // Set up color transfer function (blue to white to red)
            const colorTransferFunction = vtk.Rendering.Core.vtkColorTransferFunction.newInstance();
            colorTransferFunction.addRGBPoint(dataRange[0], 0, 0, 1); // Blue at min
            colorTransferFunction.addRGBPoint((dataRange[0] + dataRange[1]) * 0.5, 1, 1, 1); // White at middle
            colorTransferFunction.addRGBPoint(dataRange[1], 1, 0, 0); // Red at max
            mapper.setLookupTable(colorTransferFunction);

            // Add actor to renderer
            renderer.addActor(actor);

            // Add orientation widget
            const axes = vtk.Rendering.Core.vtkOrientationMarkerWidget.newInstance({
                actor: vtk.Rendering.Annotation.vtkAxesActor.newInstance(),
                interactor: renderWindow.getInteractor(),
            });
            axes.setEnabled(true);
            axes.setViewportCorner(vtk.Rendering.Core.vtkOrientationMarkerWidget.Corners.BOTTOM_RIGHT);
            axes.setViewportSize(0.15);

            // Render the scene
            renderer.resetCamera();
            renderWindow.render();
        });
    </script>
</body>
</html>
