<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Rotors Dataset Visualization</title>
    <style>
        body {
            margin: 0;
            padding: 0;
            overflow: hidden;
            background-color: #000;
        }
        #container {
            width: 100vw;
            height: 100vh;
        }
    </style>
</head>
<body>
    <div id="container"></div>

    <!-- Include vtk.js library -->
    <script type="text/javascript" src="https://unpkg.com/vtk.js@23.1.3/dist/vtk.js"></script>

    <script>
        // Create renderer and render window
        const renderer = vtk.Rendering.Core.vtkRenderer.newInstance({ background: [0, 0, 0] });
        const renderWindow = vtk.Rendering.Core.vtkRenderWindow.newInstance();
        renderWindow.addRenderer(renderer);

        // Create OpenGLRenderWindow
        const renderWindowInteractor = vtk.Rendering.Core.vtkRenderWindowInteractor.newInstance();
        renderWindowInteractor.setView(document.getElementById('container'));
        renderWindowInteractor.setRenderWindow(renderWindow);

        // Create reader for VTI dataset
        const reader = vtk.IO.Core.vtkXMLImageDataReader.newInstance();
        reader.setUrl('http://127.0.0.1:5000/dataset/rotor.vti');

        // Create mapper and actor
        const mapper = vtk.Rendering.Core.vtkDataSetMapper.newInstance();
        const actor = vtk.Rendering.Core.vtkActor.newInstance();

        // Connect pipeline
        mapper.setInputConnection(reader.getOutputPort());
        actor.setMapper(mapper);

        // Set active scalar array to "Pressure"
        mapper.setColorByArrayName('Pressure');
        mapper.setScalarModeToUsePointFieldData();

        // Create color transfer function
        const colorTransferFunction = vtk.Rendering.Core.vtkColorTransferFunction.newInstance();
        colorTransferFunction.addRGBPoint(0.0, 0.0, 0.0, 1.0);   // Blue
        colorTransferFunction.addRGBPoint(0.5, 1.0, 1.0, 1.0);   // White
        colorTransferFunction.addRGBPoint(1.0, 1.0, 0.0, 0.0);   // Red

        // Set color map
        mapper.setColorTransferFunction(colorTransferFunction);

        // Set opacity to fully opaque
        actor.getProperty().setOpacity(1.0);

        // Add actor to renderer
        renderer.addActor(actor);

        // Create slice filter
        const sliceFilter = vtk.Filters.General.vtkSlice.newInstance({
            input: reader.getOutputData(),
            origin: [0, 0, 0],
            normal: [0, 1, 0]
        });

        // Calculate slice position at 95% depth
        const bounds = reader.getOutputData().getBounds();
        const yMin = bounds[2];
        const yMax = bounds[3];
        const slicePosition = yMin + 0.95 * (yMax - yMin);
        sliceFilter.setOrigin([0, slicePosition, 0]);

        // Update mapper to use sliced data
        mapper.setInputConnection(sliceFilter.getOutputPort());

        // Create orientation marker
        const orientationWidget = vtk.Interaction.Widgets.vtkOrientationMarkerWidget.newInstance();
        orientationWidget.setEnabled(true);
        orientationWidget.setViewportCorner(vtk.Interaction.Widgets.vtkOrientationMarkerWidget.Corners.BOTTOM_RIGHT);
        orientationWidget.setOutlineSize(0.1);
        orientationWidget.setMinPixelSize(10);
        orientationWidget.setMaxPixelSize(100);

        // Create axes actor for orientation marker
        const axes = vtk.Rendering.Core.vtkAxesActor.newInstance();
        orientationWidget.setActor(axes);

        // Add orientation widget to renderer
        renderer.addActor(orientationWidget.getActor());

        // Set up camera
        renderer.resetCamera();
        renderer.getActiveCamera().setFocalPoint(0, 0, 0);
        renderer.getActiveCamera().setPosition(0, 0, 100);
        renderer.getActiveCamera().setViewUp(0, 1, 0);

        // Render
        renderWindow.render();

        // Handle window resize
        window.addEventListener('resize', () => {
            const container = document.getElementById('container');
            const width = container.clientWidth;
            const height = container.clientHeight;
            renderWindow.setSize(width, height);
            renderWindow.render();
        });

        // Start interaction
        renderWindowInteractor.initialize();
        renderWindowInteractor.start();

        // Load data when ready
        reader.loadData().then(() => {
            // Update color range based on actual data
            const dataArray = reader.getOutputData().getPointData().getArray('Pressure');
            const range = dataArray.getRange();

            colorTransferFunction.setRange(range[0], range[1]);
            colorTransferFunction.updateRange();

            // Re-render after color transfer function update
            renderWindow.render();
        });
    </script>
</body>
</html>
