            const extent = data.getExtent();
            const dims = [
                extent[1] - extent[0] + 1,
                extent[3] - extent[2] + 1,
                extent[5] - extent[4] + 1
            ];

            // Calculate slice index at 95% depth along Y axis
            const sliceIndex = Math.floor(dims[1] * 0.95);

            // Set active scalar array to "Pressure"
            data.getPointData().setActiveScalars('Pressure');

            // Create color transfer function (blue -> white -> red)
            const cfun = vtkColorTransferFunction.newInstance();
            const dataRange = data.getPointData().getScalars().getRange();
            const min = dataRange[0];
            const max = dataRange[1];

            cfun.addRGBPoint(min, 0, 0, 1);     // Blue
            cfun.addRGBPoint((min + max) / 2, 1, 1, 1); // White
            cfun.addRGBPoint(max, 1, 0, 0);     // Red

            // Create image slice
            const imageActor = vtkImageSlice.newInstance();
            const imageMapper = vtkImageMapper.newInstance();
            imageMapper.setInputData(data);
            imageMapper.setJSlice(sliceIndex);
            imageActor.setMapper(imageMapper);
            imageActor.getProperty().setRGBTransferFunction(cfun);
            imageActor.getProperty().setUseLookupTableScalarRange(true);
            imageActor.getProperty().setOpacity(1.0); // Fully opaque

            renderer.addActor(imageActor);

