    <title>Redsea Volume Rendering</title>
    <style>
        body { margin: 0; }
        #container { width: 100vw; height: 100vh; }
    </style>
</head>
<body>
    <div id="container"></div>
    <script src="https://unpkg.com/kitware-vtk.js@25.5.1/vtk.js"></script>
    <script>
        // Load required modules
        const vtkHttpDataAccessHelper = vtk.IO.Core.DataAccessHelper.vtkHttpDataAccessHelper;
        const vtkImageData = vtk.Common.DataModel.vtkImageData;
        const vtkMapper = vtk.Rendering.Core.vtkMapper;
        const vtkPiecewiseFunction = vtk.Common.DataModel.vtkPiecewiseFunction;
        const vtkColorTransferFunction = vtk.Rendering.Core.vtkColorTransferFunction;
        const vtkVolume = vtk.Rendering.Core.vtkVolume;
        const vtkVolumeMapper = vtk.Rendering.Core.vtkVolumeMapper;
        const vtkFullScreenRenderWindow = vtk.Rendering.Misc.vtkFullScreenRenderWindow;
        const vtkCalculator = vtk.Filters.General.vtkCalculator;
        const vtkMath = vtk.Common.Core.vtkMath;

        // Initialize render window
        const fullScreenRenderer = vtkFullScreenRenderWindow.newInstance({
            rootContainer: document.getElementById('container'),
            background: [0, 0, 0]
        });
        const renderer = fullScreenRenderer.getRenderer();
        const renderWindow = fullScreenRenderer.getRenderWindow();

        // Load dataset
        vtkHttpDataAccessHelper.fetchBinary('http://127.0.0.1:5000/dataset/redsea.vti')
            .then((binaryData) => {
                const imageData = vtkImageData.newInstance();
                imageData.parseAsArrayBuffer(binaryData);

                // Compute velocity magnitude
                const calculator = vtkCalculator.newInstance();
                calculator.setInputData(imageData);
                calculator.setFormula({
                    getArrays: () => ({
                        input: [{ 
                            location: 'PointData', 
                            name: 'velocity',
                            dataType: 'Float32Array',
                            numberOfComponents: 3
                        }],
                        output: [{
                            location: 'PointData',
                            name: 'velocityMagnitude',
                            dataType: 'Float32Array',
                            numberOfComponents: 1,
                            attribute: 'Scalars'
                        }]
                    }),
                    evaluate: (arraysIn, arraysOut) => {
                        const velocity = arraysIn()[0].getData();
                        const magnitude = arraysOut[0].getData();

                        for (let i = 0; i < velocity.length / 3; i++) {
                            const vx = velocity[i * 3];
                            const vy = velocity[i * 3 + 1];
                            const vz = velocity[i * 3 + 2];
                            magnitude[i] = Math.sqrt(vx*vx + vy*vy + vz*vz);
                        }
                        arraysOut[0].modified();
                    }
                });

                const result = calculator.getOutputData();

                // Setup volume rendering
                const mapper = vtkVolumeMapper.newInstance();
                mapper.setInputData(result);
                mapper.setSampleDistance(0.7);

                // Create color transfer function
                const colorTransferFunction = vtkColorTransferFunction.newInstance();
                const range = result.getPointData().getScalars().getRange();
                colorTransferFunction.addRGBPoint(range[0], 0, 0, 1); // Blue
                colorTransferFunction.addRGBPoint((range[0] + range[1]) * 0.5, 1, 1, 1); // White
                colorTransferFunction.addRGBPoint(range[1], 1, 0, 0); // Red

                // Create opacity transfer function
                const piecewiseFunction = vtkPiecewiseFunction.newInstance();
                piecewiseFunction.addPoint(range[0], 0.0);
                piecewiseFunction.addPoint(range[1] * 0.3, 0.1);
                piecewiseFunction.addPoint(range[1] * 0.7, 0.3);
                piecewiseFunction.addPoint(range[1], 0.6);

                // Create volume
                const volume = vtkVolume.newInstance();
                volume.setMapper(mapper);

                // Set volume properties
                const volumeProperty = volume.getProperty();
                volumeProperty.setRGBTransferFunction(0, colorTransferFunction);
                volumeProperty.setScalarOpacity(0, piecewiseFunction);
                volumeProperty.setInterpolationTypeToLinear();
                volumeProperty.setShade(true);
                volumeProperty.setAmbient(0.4);
                volumeProperty.setDiffuse(0.6);
                volumeProperty.setSpecular(0.2);
                volumeProperty.setSpecularPower(10);

                renderer.addVolume(volume);

                // Adjust camera
                const camera = renderer.getActiveCamera();
                const bounds = result.getBounds();
                const center = [
                    (bounds[0] + bounds[1]) / 2,
                    (bounds[2] + bounds[3]) / 2,
                    (bounds[4] + bounds[5]) / 2
                ];

                camera.setFocalPoint(center);
                camera.setPosition(center[0], center[1], center[2] + 100);
                camera.setViewUp(0, 1, 0);

                renderer.resetCamera();
                renderWindow.render();
            })
            .catch((error) => {
                console.error('Error loading dataset:', error);
            });
    </script>
</body>
</html>
