    <title>Redsea Volume Rendering</title>
    <script src="https://unpkg.com/vtk.js"></script>
</head>
<body>
    <div id="container"></div>
    <script>
        const vtkFullScreenRenderWindow = vtk.Rendering.Misc.vtkFullScreenRenderWindow;
        const vtkHttpDataSetReader = vtk.IO.Core.vtkHttpDataSetReader;
        const vtkCalculator = vtk.Filters.General.vtkCalculator;
        const vtkColorTransferFunction = vtk.Rendering.Core.vtkColorTransferFunction;
        const vtkPiecewiseFunction = vtk.Common.DataModel.vtkPiecewiseFunction;
        const vtkVolume = vtk.Rendering.Core.vtkVolume;
        const vtkVolumeMapper = vtk.Rendering.Core.vtkVolumeMapper;

        const fullScreenRenderer = vtkFullScreenRenderWindow.newInstance({
            rootContainer: document.querySelector('#container'),
            background: [0, 0, 0]
        });
        const renderer = fullScreenRenderer.getRenderer();
        const renderWindow = fullScreenRenderer.getRenderWindow();

        const reader = vtkHttpDataSetReader.newInstance({ fetchGzip: true });
        reader.setUrl('http://127.0.0.1:5000/dataset/redsea.vti').then(() => {
            const calculator = vtkCalculator.newInstance();
            calculator.setInputConnection(reader.getOutputPort());

            calculator.setFormula({
                getArrays: () => ({
                    input: [
                        {
                            name: 'velocity',
                            location: 'PointData'
                        }
                    ],
                    output: [
                        {
                            name: 'velocityMagnitude',
                            dataType: 'Float32Array',
                            numberOfComponents: 1,
                            location: 'PointData',
                            attribute: 'Scalars'
                        }
                    ]
                }),
                evaluate: (arraysIn, arraysOut) => {
                    const [velocity] = arraysIn.map(d => d.getData());
                    const [magnitude] = arraysOut.map(d => d.getData());

                    for (let i = 0; i < velocity.length / 3; i++) {
                        const vx = velocity[3 * i];
                        const vy = velocity[3 * i + 1];
                        const vz = velocity[3 * i + 2];
                        magnitude[i] = Math.sqrt(vx*vx + vy*vy + vz*vz);
                    }
                    arraysOut.forEach(a => a.modified());
                }
            });

            const mapper = vtkVolumeMapper.newInstance();
            mapper.setInputConnection(calculator.getOutputPort());
            mapper.setSampleDistance(0.7);

            const actor = vtkVolume.newInstance();
            actor.setMapper(mapper);

            const cfun = vtkColorTransferFunction.newInstance();
            const ofun = vtkPiecewiseFunction.newInstance();

            // Get scalar range after calculator updates
            calculator.update();
            const data = calculator.getOutputData();
            const range = data.getPointData().getScalars().getRange();

            // Blue -> White -> Red color map
            cfun.addRGBPoint(range[0], 0, 0, 1);
            cfun.addRGBPoint((range[0] + range[1]) / 2, 1, 1, 1);
            cfun.addRGBPoint(range[1], 1, 0, 0);

            // Opacity function
            ofun.addPoint(range[0], 0.0);
            ofun.addPoint(range[1] * 0.2, 0.0);
            ofun.addPoint(range[1] * 0.4, 0.1);
            ofun.addPoint(range[1] * 0.6, 0.3);
            ofun.addPoint(range[1] * 0.8, 0.6);
            ofun.addPoint(range[1], 1.0);

            actor.getProperty().setRGBTransferFunction(0, cfun);
            actor.getProperty().setScalarOpacity(0, ofun);
            actor.getProperty().setInterpolationTypeToLinear();
            actor.getProperty().setShade(true);
            actor.getProperty().setAmbient(0.2);
            actor.getProperty().setDiffuse(0.7);
            actor.getProperty().setSpecular(0.3);
            actor.getProperty().setSpecularPower(8.0);

            renderer.addVolume(actor);

            // Set camera to look along +Z axis
            const camera = renderer.getActiveCamera();
            camera.elevation(90);
            camera.azimuth(180);
            camera.setViewUp([0, 0, -1]);

            renderer.resetCamera();
            renderWindow.render();
        });
    </script>
</body>
</html>
