        const volume = vtk.Rendering.Core.vtkVolume.newInstance();
        volume.setMapper(volumeMapper);
        volume.setProperty(volumeProperty);
        renderer.addVolume(volume);

        // Create data loader
        const reader = vtk.IO.XML.vtkXMLImageDataReader.newInstance();

        // Load data from URL
        fetch('http://127.0.0.1:5000/dataset/redsea.vti')
            .then(response => response.arrayBuffer())
            .then(data => {
                reader.parseAsArrayBuffer(data);
                const dataset = reader.getOutputData(0);

                // Compute velocity magnitude
                const velocityArray = dataset.getPointData().getArray('velocity');
                if (velocityArray) {
                    const numPoints = velocityArray.getNumberOfTuples();
                    const magnitudeArray = vtk.Common.DataModel.vtkDataArray.newInstance({
                        numberOfComponents: 1,
                        values: new Float32Array(numPoints)
                    });
                    magnitudeArray.setName('magnitude');

                    const velocityData = velocityArray.getData();
                    const magnitudeData = magnitudeArray.getData();

                    for (let i = 0; i < numPoints; i++) {
                        const vx = velocityData[i * 3];
                        const vy = velocityData[i * 3 + 1];
                        const vz = velocityData[i * 3 + 2];
                        magnitudeData[i] = Math.sqrt(vx * vx + vy * vy + vz * vz);
                    }

                    dataset.getPointData().addArray(magnitudeArray);
                    dataset.getPointData().setActiveScalars('magnitude');
                }

                // Set volume data
                volumeMapper.setInputData(dataset);

                // Setup color transfer function
                const colorTransferFunction = vtk.Rendering.Core.vtkColorTransferFunction.newInstance();
                colorTransferFunction.addRGBPoint(0.0, 0.0, 0.0, 1.0);   // Blue
                colorTransferFunction.addRGBPoint(0.5, 1.0, 1.0, 1.0);  // White
                colorTransferFunction.addRGBPoint(1.0, 1.0, 0.0, 0.0);  // Red

                // Setup opacity transfer function
                const opacityTransferFunction = vtk.Rendering.Core.vtkPiecewiseFunction.newInstance();
                opacityTransferFunction.addPoint(0.0, 0.0);
                opacityTransferFunction.addPoint(0.2, 0.1);
                opacityTransferFunction.addPoint(0.4, 0.3);
                opacityTransferFunction.addPoint(0.6, 0.6);
                opacityTransferFunction.addPoint(1.0, 1.0);

                // Apply transfer functions to volume property
                volumeProperty.setRGBTransferFunction(0, colorTransferFunction);
                volumeProperty.setScalarOpacity(0, opacityTransferFunction);

                // Set shading parameters for realistic appearance
                volumeProperty.setShade(true);
                volumeProperty.setAmbient(0.2);
                volumeProperty.setDiffuse(0.7);
                volumeProperty.setSpecular(0.3);
                volumeProperty.setSpecularPower(10.0);

                // Set camera to look along +Z and center on dataset
                const bounds = dataset.getBounds();
                const center = [
                    (bounds[0] + bounds[1]) / 2,
                    (bounds[2] + bounds[3]) / 2,
                    (bounds[4] + bounds[5]) / 2
                ];

                const camera = renderer.getActiveCamera();
                camera.setPosition(center[0], center[1], bounds[5] + (bounds[5] - bounds[4]));
                camera.setFocalPoint(center[0], center[1], center[2]);
                camera.setViewUp(0, 1, 0);
                camera.setParallelProjection(false);

                // Render
                renderer.resetCamera();
                renderWindow.render();
            })
            .catch(error => {
                console.error('Error loading dataset:', error);
            });

        // Start interaction
        renderWindowInteractor.initialize();
        renderWindowInteractor.start();
    </script>
</body>
</html>
