                const calculator = vtkCalculator.newInstance();
                calculator.setFormula({
                    getArrays: (inputDataSets) => ({
                        input: [{ location: vtk.Common.DataModel.vtkDataSet.FieldDataTypes.POINT }],
                        output: [
                            {
                                location: vtk.Common.DataModel.vtkDataSet.FieldDataTypes.POINT,
                                name: 'velocity_magnitude',
                                dataType: 'Float32Array',
                                numberOfComponents: 1,
                            },
                        ],
                    }),
                    evaluate: (arraysIn, arraysOut) => {
                        const [velocity] = arraysIn.map((d) => d.getData());
                        const [magnitude] = arraysOut.map((d) => d.getData());

                        for (let i = 0, sz = velocity.length / 3; i < sz; ++i) {
                            const vx = velocity[i * 3];
                            const vy = velocity[i * 3 + 1];
                            const vz = velocity[i * 3 + 2];
                            magnitude[i] = Math.sqrt(vx * vx + vy * vy + vz * vz);
                        }

                        arraysOut.forEach((x) => x.modified());
                    },
                });

                calculator.setInputData(data);
                calculator.update();
                const computedData = calculator.getOutputData();

                // Set velocity magnitude as active scalar
                const pointData = computedData.getPointData();
                const velocityArray = pointData.getArray('velocity');
                const magnitudeArray = pointData.getArray('velocity_magnitude');
                pointData.setScalars(magnitudeArray);

