        const vtkImageData = vtk.Common.DataModel.vtkImageData;
        const vtkOutlineFilter = vtk.Filters.General.vtkOutlineFilter;
        const vtkImageStreamline = vtk.Filters.General.vtkImageStreamline;
        const vtkPlaneSource = vtk.Filters.Sources.vtkPlaneSource;
        const vtkActor = vtk.Rendering.Core.vtkActor;
        const vtkMapper = vtk.Rendering.Core.vtkMapper;
        const vtkProperty = vtk.Rendering.Core.vtkProperty;
        const vtkFullScreenRenderWindow = vtk.Rendering.Misc.vtkFullScreenRenderWindow;
        const vtkHttpDataSetReader = vtk.IO.Core.vtkHttpDataSetReader;

        // Create renderer and render window
        const renderWindow = vtk.Rendering.Core.vtkRenderWindow.newInstance();
        const renderer = vtk.Rendering.Core.vtkRenderer.newInstance({ background: [0, 0, 0] });
        renderWindow.addRenderer(renderer);

        // Create full screen render window
        const fullScreenRenderer = vtkFullScreenRenderWindow.newInstance({
            renderWindow,
            container: document.getElementById('container')
        });

        // Create reader for Isabel dataset
        const reader = vtkHttpDataSetReader.newInstance({ fetchGzip: true });
        reader.setUrl('http://127.0.0.1:5000/dataset/isabel.vti');

        // Create streamline filter
        const streamline = vtkImageStreamline.newInstance();
        streamline.setIntegrationStep(0.01);
        streamline.setNumberOfSteps(1000);
        streamline.setSeedType(2); // Point source

        // Create seed points (center of dataset)
        const planeSource = vtkPlaneSource.newInstance();
        planeSource.setOrigin(0, 0, 0);
        planeSource.setPoint1(1, 0, 0);
        planeSource.setPoint2(0, 1, 0);
        planeSource.setResolution(20, 20); // High density seed points

        // Create outline filter
        const outline = vtkOutlineFilter.newInstance();

        // Create actors
        const streamlineActor = vtkActor.newInstance();
        const outlineActor = vtkActor.newInstance();

        // Setup streamline mapper
        const streamlineMapper = vtkMapper.newInstance();
        streamlineMapper.setInputConnection(streamline.getOutputPort());
        streamlineActor.setMapper(streamlineMapper);
        streamlineActor.getProperty().setColor(0, 1, 1); // Cyan
        streamlineActor.getProperty().setLineWidth(2);

        // Setup outline mapper
        const outlineMapper = vtkMapper.newInstance();
        outlineMapper.setInputConnection(outline.getOutputPort());
        outlineActor.setMapper(outlineMapper);
        outlineActor.getProperty().setColor(1, 0, 0); // Red
        outlineActor.getProperty().setLineWidth(2);

        // Add actors to renderer
        renderer.addActor(streamlineActor);
        renderer.addActor(outlineActor);

        // Function to process data after loading
        reader.loadData().then(() => {
            const dataset = reader.getOutputData(0);

            // Set input for streamline filter
            streamline.setInputData(dataset);
            streamline.setVectorArrayName('Velocity');

            // Set input for outline filter
            outline.setInputData(dataset);

            // Set seed points
            const extent = dataset.getExtent();
            const center = [
                (extent[0] + extent[1]) / 2,
                (extent[2] + extent[3]) / 2,
                (extent[4] + extent[5]) / 2
            ];

            // Adjust plane source to be centered
            const spacing = dataset.getSpacing();
            const origin = [
                center[0] - 5 * spacing[0],
                center[1] - 5 * spacing[1],
                center[2] - 5 * spacing[2]
            ];

            const point1 = [
                center[0] + 5 * spacing[0],
                center[1] - 5 * spacing[1],
                center[2] - 5 * spacing[2]
            ];

            const point2 = [
                center[0] - 5 * spacing[0],
                center[1] + 5 * spacing[1],
                center[2] - 5 * spacing[2]
            ];

            planeSource.setOrigin(origin);
            planeSource.setPoint1(point1);
            planeSource.setPoint2(point2);

            // Connect plane source to streamline as seed
            streamline.setSeedData(planeSource.getOutputData());

            renderer.resetCamera();
            renderWindow.render();
        });
    </script>
