    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Redsea Volume Rendering</title>
    <style>
        body, html {
            margin: 0;
            padding: 0;
            width: 100%;
            height: 100%;
            overflow: hidden;
            background-color: #000;
        }
        #container {
            width: 100%;
            height: 100%;
        }
    </style>
</head>
<body>
    <div id="container"></div>
    <script src="https://unpkg.com/vtk.js"></script>
    <script>
        // Load the dataset
        const xhr = new XMLHttpRequest();
        xhr.open('GET', 'http://127.0.0.1:5000/dataset/redsea.vti', true);
        xhr.responseType = 'arraybuffer';

        xhr.onload = function() {
            if (xhr.status === 200) {
                // Parse the VTI file
                const parser = new vtk.IO.XML.vtkXMLImageDataReader();
                parser.parseAsArrayBuffer(xhr.response);

                const dataset = parser.getOutputData(0);

                // Compute velocity magnitude
                const calculator = vtk.Filters.General.vtkCalculator.newInstance();
                calculator.setFormula({
                    getArrays: (inputDataSets) => ({
                        input: [{ location: vtk.Common.DataModel.vtkDataSet.FieldAssociations.POINT }],
                        output: [{
                            location: vtk.Common.DataModel.vtkDataSet.FieldAssociations.POINT,
                            name: 'velocity_magnitude',
                            dataType: 'Float32Array',
                            numberOfComponents: 1
                        }]
                    }),
                    evaluate: (arraysIn, arraysOut) => {
                        const [velocity] = arraysIn.map(d => d.getData());
                        const [magnitude] = arraysOut.map(d => d.getData());

                        for (let i = 0; i < velocity.length; i += 3) {
                            const vx = velocity[i];
                            const vy = velocity[i + 1];
                            const vz = velocity[i + 2];
                            magnitude[i / 3] = Math.sqrt(vx * vx + vy * vy + vz * vz);
                        }

                        arraysOut.forEach(x => x.modified());
                    }
                });

                calculator.setInputData(dataset);
                calculator.update();

                const magnitudeDataset = calculator.getOutputData();

                // Setup volume rendering
                const volume = vtk.Rendering.Volume.vtkVolume.newInstance();
                const volumeMapper = vtk.Rendering.Volume.vtkFixedPointVolumeRayCastMapper.newInstance();
                const volumeProperty = vtk.Rendering.Core.vtkVolumeProperty.newInstance();

                volumeMapper.setInputData(magnitudeDataset);
                volume.setMapper(volumeMapper);

                // Color transfer function (blue -> white -> red)
                const colorFun = vtk.Rendering.Core.vtkColorTransferFunction.newInstance();
                colorFun.addRGBPoint(0.0, 0.0, 0.0, 1.0);   // Blue
                colorFun.addRGBPoint(0.5, 1.0, 1.0, 1.0);   // White
                colorFun.addRGBPoint(1.0, 1.0, 0.0, 0.0);   // Red

                // Opacity transfer function
                const opacityFun = vtk.Common.DataModel.vtkPiecewiseFunction.newInstance();
                opacityFun.addPoint(0.0, 0.0);
                opacityFun.addPoint(0.5, 0.5);
                opacityFun.addPoint(1.0, 1.0);

                volumeProperty.setRGBTransferFunction(0, colorFun);
                volumeProperty.setScalarOpacity(0, opacityFun);

                // Shading properties
                volumeProperty.setShade(true);
                volumeProperty.setAmbient(0.2);
                volumeProperty.setDiffuse(0.8);
                volumeProperty.setSpecular(0.3);
                volumeProperty.setSpecularPower(128);

                volume.setProperty(volumeProperty);

                // Setup renderer
                const renderer = vtk.Rendering.Core.vtkRenderer.newInstance({ background: [0, 0, 0] });
                const renderWindow = vtk.Rendering.Core.vtkRenderWindow.newInstance();
                renderWindow.addRenderer(renderer);

                const container = document.getElementById('container');
                const renderWindowContainer = vtk.Rendering.Misc.vtkRenderWindowContainer.newInstance();
                renderWindowContainer.setContainer(container);
                renderWindowContainer.setRenderWindow(renderWindow);

                renderer.addVolume(volume);

                // Set camera to look along +Z and center on dataset
                const bounds = dataset.getBounds();
                const center = [
                    (bounds[0] + bounds[1]) / 2,
                    (bounds[2] + bounds[3]) / 2,
                    (bounds[4] + bounds[5]) / 2
                ];

                renderer.getActiveCamera().setFocalPoint(...center);
                renderer.getActiveCamera().setPosition(center[0], center[1], center[2] + 100);
                renderer.getActiveCamera().setViewUp(0, 1, 0);
                renderer.resetCamera();

                renderer.resetCameraClippingRange();
                renderWindow.render();
            }
        };

        xhr.send();
    </script>
</body>
</html>
